import { useEffect, useState } from "react";
import {
  BrowserRouter as Router,
  Routes,
  Route,
} from "react-router-dom";
import "./App.css";
import "bootstrap/dist/css/bootstrap.min.css";
import NavBar from "./NavBar";
import RecipesList from "./RecipesList";
import Recipe from "./Recipe";
import About from "./About";
import Home from "./Home";
import ErrorPage from "./ErrorPage";
import { Spinner } from "reactstrap";

function App() {
  const [recipeData, setRecipeData] = useState([]);
  const [userInput, setUserInput] = useState("");

  const API_KEY = process.env.REACT_APP_API_KEY;
  const url = `https://api.spoonacular.com/recipes/complexSearch?apiKey=${API_KEY}&includeIngredients=${userInput
    .trim()
    .split(/\s+/)
    .join("+")}&number=5&diet=vegan`;

  useEffect(() => {
    fetch(url)
      .then((res) => res.json())
      .then((data) => setRecipeData(data));
  }, [userInput]);

  if (!recipeData) {
    return (
      <Spinner color="black" size="">
        Loading...
      </Spinner>
    )
  }

  return (
    <Router>
      <NavBar />

      <Routes>
        <Route path="/" element={<Home setUserInput={setUserInput} />} />
        <Route path="/about" element={<About />} />
        <Route path="/recipe/:id" element={<Recipe data={recipeData} />} />
        <Route
          path="/recipes-list"
          element={<RecipesList data={recipeData} />}
        />
        <Route path="*" element={<ErrorPage />} />
      </Routes>
    </Router>
  );
}

export default App;
